import { Box, Button, Flex, Heading } from "@chakra-ui/react";
import { Form, Formik, FormikHelpers } from "formik";
import { withUrqlClient } from "next-urql";
import React from "react";
import { FormInput, FormInputPassword } from "../components/FormInput";
import {
  validateEmail,
  validatePassword,
  validateUsername,
} from "../components/validations/validateUsername";
import { useRegisterMutation } from "../graphql/generated";
import { formBreakPoints } from "../styles/breakpoints";
import { clientOptions } from "../utils/createClient";

interface Props {}

interface RegisterValues {
  username: string;
  email: string;
  password: string;
  repeat_password: string;
}

const Register = (props: Props) => {
  const [{ fetching }, register] = useRegisterMutation();
  const sendData = () => {};
  return (
    <Box h="100vh">
      <Flex w="100%" h="100%" justifyContent="center" alignItems="center">
        <Formik
          initialValues={{
            username: "",
            email: "",
            password: "",
            repeat_password: "",
          }}
          onSubmit={(
            values: RegisterValues,
            { setSubmitting }: FormikHelpers<RegisterValues>
          ) => {
            console.log(values);
            /* register(); */
          }}
        >
          {({ isSubmitting }) => (
            <Box w={formBreakPoints}>
              <Heading>Registro</Heading>
              <Form>
                <FormInput
                  name="username"
                  type="text"
                  label="Usuario"
                  placeholder="Inserta tu usuario"
                  required={true}
                  validation={validateUsername}
                />

                <FormInput
                  name="email"
                  type="text"
                  label="Email"
                  placeholder="Inserta tu email"
                  required={true}
                  validation={validateEmail}
                />
                <FormInputPassword
                  name="password"
                  type="password"
                  label="Contraseña"
                  placeholder="Inserta tu contraseña"
                  required={true}
                  validation={validatePassword}
                />

                <FormInputPassword
                  name="repeat_password"
                  type="password"
                  label="Reptetir contraseña"
                  placeholder="Repite tu contraseña"
                  required={true}
                  validation={() => validateUsername()}
                />

                <Button
                  mt={4}
                  colorScheme="teal"
                  isLoading={isSubmitting}
                  type="submit"
                >
                  Submit
                </Button>
              </Form>
            </Box>
          )}
        </Formik>
      </Flex>
    </Box>
  );
};

export default withUrqlClient(clientOptions, { ssr: false })(Register);
