import { Flex, Box, Heading, Button, Icon, Text, Link } from "@chakra-ui/react";
import React from "react";
import { formBreakPoints } from "../../styles/breakpoints";
import { FacebookIcon, GoogleIcon } from "../../styles/icons";
import { FacebookLoginButton } from "../FacebookLoginButton";
import { GoogleLoginButton } from "../GoogleLoginButton";

interface Props {
  title: string;
  children: JSX.Element;
  type: "login" | "signup";
}

export const AuthFormLayout = ({ title, children, type }: Props) => {
  return (
    <Flex
      w="100%"
      h="100vh"
      p={["0", "0", "0", "3em", "3em"]}
      justifyContent="center"
      alignItems="center"
    >
      <Flex
        zIndex={0}
        order={1}
        h="100%"
        w={["100%", "100%", "100%", "50%", "60%"]}
        p="2em"
        bgImage="url('/images/auhtscreen.jpg')"
        bgSize="cover"
        bgPos="center"
        alignItems="flex-end"
        position={["absolute", "absolute", "absolute", "static", "static"]}
      >
        <Heading textColor="#ffffff">Bienvenido</Heading>
      </Flex>
      <Box
        h="90vh"
        w={["90%", "90%", "90%", "50%", "40%"]}
        bgColor="#ffffff"
        zIndex={1}
      >
        <Box m="6em">
          <Heading fontSize="2.5em" mb="1em" letterSpacing="wider">
            {title}
          </Heading>
          {children}
          <Box mt="2em" textAlign="center">
            <Text variant="smallText">or</Text>
          </Box>
          <Flex justifyContent="space-between" mt="2em">
            <GoogleLoginButton />
            {/*  <Button
              size="xl"
              w="45%"
              variant="whiteWithBorder"
              leftIcon={<FacebookIcon />}
            >
              Facebook
            </Button> */}
            {/*  <Button
              size="xl"
              w="45%"
              variant="whiteWithBorder"
              leftIcon={<GoogleIcon />}
            >
              Google
            </Button> */}
          </Flex>
          <Box mt="2em">
            <Link>
              <Text variant="smallText" textAlign="center">
                {type === "login" ? "Crear cuenta" : "Iniciar sesión"}
              </Text>
            </Link>
          </Box>
        </Box>
      </Box>
    </Flex>
  );
};
