export const validateUsername = (value?: string): any => {
  let error: string;
  if (!value) {
    error = "El usuario es requerido";
  } else if (value.length < 3) {
    error = "El usuario debe tener al menos 3 letras";
  }
  return error;
};

export const validateEmail = (value?: string): String => {
  const reg = RegExp(/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g);
  let error: string;
  if (!value) {
    error = "El email es requerido";
  } else if (!reg.test(value)) {
    error = "Inserta un email correcto";
  }
  return error;
};

export const validatePassword = (value?: string): String => {
  const reg = RegExp(/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/gm);
  let error: string;
  if (!value) {
    error = "La contraseña es requerida";
  } else if (!reg.test(value)) {
    error = `La contraseña debe ener una longitud de al menos 8 caracteres.
    Contenter un número.\n
    Contener un símbolo.\n
    Contener una mayúsucla.\n
    `;
  }
  return error;
};
