import { extendTheme } from "@chakra-ui/react";

export const theme = extendTheme({
  components: {
    Button: {
      baseStyle: {
        borderRadius: 0,
        fontWeight: 500,
      },
      variants: {
        black: {
          bg: "#000000",
          color: "#ffffff",
        },
      },
      defaultProps: {
        variant: "black",
      },
    },
  },
});
