import { Box, Button, Flex, Text, Link } from "@chakra-ui/react";
import { Form, Formik, FormikHelpers } from "formik";
import { withUrqlClient } from "next-urql";
import React from "react";
import { FormInput, FormInputPassword } from "../components/FormInput";
import { AuthFormLayout } from "../components/layouts/authFormLayout";
import {
  validateEmail,
  validatePassword,
  validateUsername,
} from "../components/validations/validateUsername";
import { useRegisterMutation } from "../graphql/generated";
import { formBreakPoints } from "../styles/breakpoints";
import { clientOptions } from "../utils/createClient";

interface RegisterValues {
  username: string;
  email: string;
  password: string;
  repeat_password: string;
}

const Register = () => {
  const [{ fetching }, register] = useRegisterMutation();
  const sendData = () => {};
  return (
    <Formik
      initialValues={{
        username: "",
        email: "",
        password: "",
        repeat_password: "",
      }}
      onSubmit={(
        values: RegisterValues,
        { setSubmitting }: FormikHelpers<RegisterValues>
      ) => {
        console.log(values);
        register({ username, email, password });
      }}
    >
      {({ isSubmitting }) => (
        <AuthFormLayout title="Registro" type="signup">
          <Form>
            <FormInput
              name="username"
              type="text"
              label="Usuario"
              placeholder="Inserta tu usuario"
              required={true}
              validation={validateUsername}
            />

            <FormInput
              name="email"
              type="text"
              label="Email"
              placeholder="Inserta tu email"
              required={true}
              validation={validateEmail}
            />
            <FormInputPassword
              name="password"
              type="password"
              label="Contrase単a"
              placeholder="Inserta tu contrase単a"
              required={true}
              validation={validatePassword}
            />

            <FormInputPassword
              name="repeat_password"
              type="password"
              label="Reptetir contrase単a"
              placeholder="Repite tu contrase単a"
              required={true}
              validation={validatePassword}
            />
            <Box w="100%" textAlign="right" mb="1em">
              <Link>
                <Text variant="smallText">{"Ya tienes una cuenta?"}</Text>
              </Link>
            </Box>

            <Button
              size="xl"
              mt={4}
              isFullWidth
              colorScheme="teal"
              isLoading={isSubmitting}
              type="submit"
            >
              Submit
            </Button>
          </Form>
        </AuthFormLayout>
      )}
    </Formik>
  );
};

export default withUrqlClient(clientOptions, { ssr: false })(Register);
