import { Box, Button, Flex } from "@chakra-ui/react";
import { Form, Formik, FormikHelpers } from "formik";
import { withUrqlClient } from "next-urql";
import React from "react";
import { FormInput, FormInputPassword } from "../components/FormInput";
import { useRegisterMutation } from "../graphql/generated";
import { formBreakPoints } from "../styles/breakpoints";
import { clientOptions } from "../utils/createClient";

interface Props {}

interface RegisterValues {
  username: string;
  email: string;
  password: string;
}

const Register = (props: Props) => {
  const [{ fetching }, register] = useRegisterMutation();
  const sendData = () => {};
  return (
    <Box
      h="100vh"
      bgImage="url('illustrations/auth_illustration.svg')"
      bgPos="center"
      bgRepeat="no-repeat"
      bgSize="100%"
    >
      <Flex w="100%" h="100%" justifyContent="center" alignItems="center">
        <Formik
          initialValues={{
            username: "",
            email: "",
            password: "",
          }}
          onSubmit={(
            values: RegisterValues,
            { setSubmitting }: FormikHelpers<RegisterValues>
          ) => {
            console.log(values);
            /* register(); */
          }}
        >
          {({ isSubmitting }) => (
            <Box w={formBreakPoints}>
              <Form>
                <FormInput
                  name="username"
                  type="text"
                  label="Usuario"
                  placeholder="Inserta tu usuario"
                />

                <FormInput
                  name="email"
                  type="text"
                  label="Email"
                  placeholder="Inserta tu email"
                />
                <FormInputPassword
                  name="password"
                  type="password"
                  label="Contrase単a"
                  placeholder="Inserta tu contrase単a"
                />

                <FormInputPassword
                  name="repeat-password"
                  type="password"
                  label="Reptetir ontrase単a"
                  placeholder="Repite tu contrase単a"
                />

                <Button
                  mt={4}
                  colorScheme="teal"
                  isLoading={isSubmitting}
                  type="submit"
                >
                  Submit
                </Button>
              </Form>
            </Box>
          )}
        </Formik>
      </Flex>
    </Box>
  );
};

export default withUrqlClient(clientOptions, { ssr: false })(Register);
