import { extendTheme } from "@chakra-ui/react";

export const theme = extendTheme({
  fonts: {
    body: "'LibreFranklin'",
    heading: "'Fraunces'",
  },

  components: {
    FormLabel: {
      baseStyle: {
        fontWeight: 600,
        fontSize: "0.8em",
      },
    },
    Button: {
      baseStyle: {
        borderRadius: 0,
        fontWeight: "medium",
      },
      variants: {
        black: {
          bg: "#000000",
          color: "#ffffff",
        },
      },
      defaultProps: {
        variant: "black",
      },
    },
    Input: {
      baseStyle: {
        padding: "10em",
      },
    },
  },
});
