import {
  Box,
  Button,
  Center,
  Flex,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Input,
  useBreakpoint,
  useBreakpointValue,
} from "@chakra-ui/react";
import { Formik, FormikHelpers, Form, Field } from "formik";
import { withUrqlClient } from "next-urql";
import React from "react";
import FormInput from "../components/FormInput";
import { useRegisterMutation } from "../graphql/generated";
import { clientOptions } from "../utils/createClient";

interface Props {}

interface RegisterValues {
  username: string;
  email: string;
  password: string;
}

const Register = (props: Props) => {
  const [{ fetching }, register] = useRegisterMutation();
  const sendData = () => {};
  return (
    <Box
      h="100vh"
      bgImage="url('illustrations/auth_illustration.svg')"
      bgPos="center"
      bgRepeat="no-repeat"
      bgSize="100%"
    >
      <Flex w="100%" h="100%" justifyContent="center" alignItems="center">
        <Formik
          initialValues={{
            username: "",
            email: "",
            password: "",
          }}
          onSubmit={(
            values: RegisterValues,
            { setSubmitting }: FormikHelpers<RegisterValues>
          ) => {
            console.log(values);
            /* register(); */
          }}
        >
          {({ isSubmitting }) => (
            <Box width="100%">
              <Form>
                <FormInput
                  name="username"
                  type="text"
                  label="Usuario"
                  placeholder="Inserta tu usuario"
                />

                <FormInput
                  name="email"
                  type="text"
                  label="Email"
                  placeholder="Inserta tu email"
                />
                <Button
                  mt={4}
                  colorScheme="teal"
                  isLoading={isSubmitting}
                  type="submit"
                >
                  Submit
                </Button>
              </Form>
            </Box>
          )}
        </Formik>
      </Flex>
    </Box>
  );
};

export default withUrqlClient(clientOptions, { ssr: false })(Register);
