import { Flex, Box, Heading } from "@chakra-ui/react";
import React from "react";
import { formBreakPoints } from "../../styles/breakpoints";

export const AuthFormLayout = ({ title, children }) => {
  return (
    <Flex
      w={formBreakPoints}
      h="100vh"
      p="3em"
      justifyContent="center"
      alignItems="center"
    >
      <Flex
        order={1}
        h="100%"
        w="60%"
        p="2em"
        bgImage="url('/images/auhtscreen.jpg')"
        bgSize="cover"
        bgPos="center"
        alignItems="flex-end"
      >
        <Heading textColor="#ffffff">Bienvenido</Heading>
      </Flex>
      <Box h="90vh" w="40%">
        <Box m="6em">
          <Heading fontSize="2.5em" mb="1em" letterSpacing="wider">
            {title}
          </Heading>
          {children}
          <Box textAlign="center">or</Box>
          <Flex></Flex>
        </Box>
      </Box>
    </Flex>
  );
};
