export const validateUsername = (value?: string): String => {
  let error: string;
  if (!value) {
    error = "El usuario es requerido";
  } else if (value.length < 3) {
    error = "El usuario debe tener al menos 3 letras";
  }
  return error;
};

export const emailValidation = (value?: string): String => {
  const reg = RegExp(/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g);
  let error: string;
  if (!value) {
    error = "El email es requerido";
  } else if (!reg.test(value)) {
    error = "Inserta un email correcto";
  }
  return error;
};
