import { extendTheme } from "@chakra-ui/react";

export const theme = extendTheme({
  components: {
    Button: {
      baseStyle: {
        borderRadius: 0,
      },
      variants: {
        black: "#000000",
      },
      defaultProps: {
        variant: "black",
      },
    },
  },
});
