import { Flex, Box, Heading } from "@chakra-ui/react";
import React from "react";
import { formBreakPoints } from "../../styles/breakpoints";

export const AuthFormLayout = ({ children }) => {
  return (
    <Box h="100vh">
      <Flex
        w={formBreakPoints}
        m="3em"
        justifyContent="center"
        alignItems="center"
      >
        <Box
          order={1}
          h="100%"
          w="100%"
          bgImage="url('/images/auhtscreen.jpg')"
          bgSize="cover"
          bgPos="center"
        ></Box>
        <Box h="90vh" w="40vw">
          <Box m="6em">
            <Heading mb="1em">Registro</Heading>
            {children}
          </Box>
        </Box>
      </Flex>
    </Box>
  );
};
